import React, { Component } from 'react';
import axios from 'axios';
import Post from '../../components/Post/Post';
import FullPost from '../../components/FullPost/FullPost';
import NewPost from '../../components/NewPost/NewPost';
import './Blog.css';

class Blog extends Component {

    state = {
        posts: [],
        selectedPostID: null,
        error: false
    }
    //This lifecycle is best for http requests as HTTP
    //Requests are Side Effects
    componentDidMount(){
      axios.get('https://jsonplaceholder.typicode.com/posts')
      .then(response => {
        const fourposts = response.data.slice(0, 4); //Get only first 4 responses
        const updatedPosts = fourposts.map(mypost => {
            return {
                ...mypost,
                author: 'Namit'
            }
        });
         this.setState({posts:updatedPosts});
      })
      .catch(error =>{ //Error Handler
        this.setState({error: true});
        console.log(error);
      });
    }

    postSelectedHandler = (id) =>{
       this.setState({selectedPostID: id});
    }

    render () {
        let jsonposts = <p style={{textAlign: 'center'}}>Something Went Wrong</p>;
        if(!this.state.error){
          jsonposts = this.state.posts.map(mypost => {
          return <Post 
          title={mypost.title} 
          key={mypost.id} 
          author={mypost.author}
          clicked={()=>this.postSelectedHandler(mypost.id)/*Gets the ID of post clicked*/}/>;
        });
        }
        return (
            <div>
            <header> 
              <nav>
                <ul>
                <li><a href='./'>Link1</a></li>
                  </ul>
               </nav></header>
                <section className="Posts">
                   {jsonposts}
                </section>
                <section>
                    <FullPost postid={this.state.selectedPostID} />
                </section>
                <section>
                    <NewPost />
                </section>
            </div>
        );
    }
}

export default Blog;
